sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var uno= new GlideDateTime(current.fecha_despido);
	var dos= new GlideDateTime(current.fecha_contrato);
	var estado=current.estado;

	if(estado==5){
		var duration= GlideDateTime.subtract(dos,uno); 
		var a = duration.getDisplayValue();
		var array=a.split();
		var dias;
		dias=parseInt(array[0]);
		var agnos=dias/365;


		if(agnos<1){
			current.indemnizacion=600;
		}else if(agnos>=1 && agnos<2){
			current.indemnizacion=1000;
		}else if(agnos>=2 && agnos < 3){
			current.indemnizacion=2000;
		}else if(agnos>=3 && agnos < 5){
			current.indemnizacion=4000;
		}else if(agnos>=5 && agnos < 10){
			current.indemnizacion=7500;
		}else if(agnos >= 10){

			current.indemnizacion=10000;
		}else{
			gs.addInfoMessage("Lo lamentamos, ha ocurrido un error inesperado.");
		}

		current.update();
	}






})(current, previous);
